import{_ as t,c as a,a as o,o as n}from"./app.d8c142fb.js";const g='{"title":"What is Swagger-PHP?","description":"","frontmatter":{},"headers":[],"relativePath":"guide/index.md"}',s={};function i(r,e,d,c,p,l){return n(),a("div",null,e[0]||(e[0]=[o('<h1 id="what-is-swagger-php" tabindex="-1">What is Swagger-PHP? <a class="header-anchor" href="#what-is-swagger-php" aria-hidden="true">#</a></h1><p><code>swagger-php</code> is a library that extracts API metadata from your PHP source code files.</p><p>The idea is to add <code>swagger-php</code> <a href="./annotations.html">annotations</a> or <a href="./attributes.html">attributes</a> next to the relevant PHP code in your application. These will contain the details about your API and <code>swagger-php</code> will convert those into machine-readable <a href="https://spec.openapis.org/oas/v3.1.0.html" target="_blank" rel="noopener noreferrer">OpenAPI documentation</a>.</p><p>By adding your API documentation next to the corresponding source code (same file!) makes it easy to keep it up-to-date as all details can be modified in one place.</p><div class="tip custom-block"><p class="custom-block-title">Annotating vs. Annotations</p><p>When talking about annotating your code we mean the act of adding meta-data to your codebase. This can be done by either adding <a href="./annotations.html"><code>Annotations</code></a> or <a href="./attributes.html"><code>Attributes</code></a>.</p></div><div class="warning custom-block"><p class="custom-block-title">Requirements</p><p>Using <code>swagger-php</code> requires a minimum of <strong>PHP\xA07.2</strong> for using annotations and at least <strong>PHP\xA08.1</strong> to use attributes.</p></div>',6)]))}var u=t(s,[["render",i]]);export{g as __pageData,u as default};
